var App = require('wish-app').App;
var UcpProtocol = require('./ucp-protocol.js').UcpProtocol;

function UcpApp(descriptor) {
    var name = descriptor ? descriptor.name || 'Mist App' : 'Mist App';
    this.ucp = new UcpProtocol();
    this.app = new App(name, [this.ucp.ucp]);
}

UcpApp.prototype.attachUcpDevice = function(device) {
    this.ucp.attachUcpDevice(device);
};


module.exports = {
    UcpApp: UcpApp };
