var util = require("util");
var EventEmitter = require("events").EventEmitter;

/*
 * This is a simple HW simulator
 */

function DummyDevice() {
    var self = this; 
    this.deviceData = {};
}

util.inherits(DummyDevice, EventEmitter);

DummyDevice.prototype.write = function(feature, value) {
    this.deviceData[feature] = value;
    this.emit('change', feature, value);
};

module.exports = {
    DummyDevice: DummyDevice};